import math


class SumTwo:
	
	def __init__(self, x, y):
		self.x = x
		self.y = y
	
	def sumtwovar(self):
		"""
		this function is just a simple function to sum two vairables
		"""
		return self.x + self.y
	
	def squarootvar(self) -> object:
		"""
		this function is just a simple function to sum two vairables
		"""
		return math.sqrt(self.x)